function screenHeight(){return $.browser.opera?window.innerHeight:$(window).height();}function screenWidth(){return $.browser.opera?window.innerWidth:$(window).width();}function send_ajax(options){if(options.data==undefined)options.data={};$.ajax({url:options.url,data:options.data,type:'POST',dataType:"json",success:function(data,textStatus,xhr){if(data.reply=="1"){if(options.on_success!=undefined)options.on_success(data);}else{showNotice(data.message,true);}},error:function(xhr,status,error){showNotice("Произошла ошибка!",true);if(options.on_error!=undefined)options.on_error();},beforeSend:function(xhr){$('.status_bar').stop().fadeIn('fast');if(options.on_before_send!=undefined)options.on_before_send();},complete:function(xhr,status){$('.status_bar').stop().fadeOut('fast');if(options.on_complete!=undefined)options.on_complete();}});}function isValidEmail(email){var re=/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;return re.test(email);}